package org.japo.java.pll.command.general;

import java.io.IOException;
import java.util.List;
import javax.servlet.ServletException;
import org.japo.java.entities.Grupo;
import org.japo.java.entities.Usuario;
import org.japo.java.pll.command.Command;

/**
 *
 * @author Luis Alejandro Rodríguez Fernández -
 * luisalejandro.rodriguez.alum@iescamp.es
 */
public class CommandSignup extends Command{

    final String AVATAR = "data:image/png;base64,";
    final int grupo = Grupo.BASICO;

    @Override
    public void process() throws ServletException, IOException {
        // JSP
        String page;

        try {
//            // Obtener Operación
            String op = request.getParameter("op");
            List<Usuario> listaUsr = bll.obtenerListaUsuario();

            if (op == null || op.equals("captura")) {
                page = "admin/signup";
            } else if (op.equals("proceso")) {
                // Procesar Signup
                String user = request.getParameter("user");
                String pass = request.getParameter("pass");
                String pass2 = request.getParameter("pass2");
                String email = request.getParameter("email");

                boolean passOK = pass.equals(pass2);
                boolean userOK = true;

                for (Usuario u : listaUsr) {
                    if (u.getUser().equalsIgnoreCase(user)) {
                        userOK = false;
                    }
                }

                if (passOK && userOK && Usuario.validarUsername(user) && Usuario.validarPassword(pass)) {
                    // Instanciar Entidad
                    Usuario u = new Usuario(0, user, pass, email, AVATAR, grupo);

                    // Formulario > Usuario Insertado - true | false
                    boolean procesoOK = bll.insertarUsuario(u);

                    // Validar el Borrado
                    page =  "redirect/redirect-landing";
                } else if (!userOK) {
                    System.out.println("user problem");
                    page = "operacion-cancelada";
                } else {
                    System.out.println("pass problem");
                    page = "operacion-cancelada";
                }
            } else {
                // Recurso NO disponible
                page = "error/page404";
            }

        } catch (Exception e) {
            // Recurso NO Disponible
            System.out.println(e.getMessage());
            page = "error/page404";
        }

        // Redirección JSP
        forward(page);
    }
}
